<script setup>
import {ref, watch} from 'vue';

// 图片处理自定义
const defaultImg = "data:image/png;base64,"
const img = ref('')
const uploadFile = info => {
  // 上传图片后，读取文件并转换成base64格式显示在本地
  let file = info.target.files[0]
  let fileReader = new FileReader();
  fileReader.readAsDataURL(file)
  fileReader.onload = () => {
    img.value = fileReader.result.toString()
  }

  clipKey.value = {
    leftTopX: 0,
    leftTopY: 0,
    leftBottomX: 0,
    leftBottomY: 128,
    rightTopX: 128,
    rightTopY: 0,
    rightBottomX: 128,
    rightBottomY: 128,
  }
}
let clipKey = ref({
  leftTopX: 0,
  leftTopY: 0,
  leftBottomX: 0,
  leftBottomY: 128,
  rightTopX: 128,
  rightTopY: 0,
  rightBottomX: 128,
  rightBottomY: 128,
})
let crop = ref('')
const mouseLeave = ref({
  keydown: false,
  spx: '',
  spy: '',
})
const boxMove = (e) => {
  mouseLeave.value.keydown = true
  mouseLeave.value.spx = e.offsetX
  mouseLeave.value.spy = e.offsetY

}

const mouseMove = (e) => {
  if (mouseLeave.value.keydown) {
    let x = e.offsetX - mouseLeave.value.spx
    let y = e.offsetY - mouseLeave.value.spy
    if (clipKey.value.rightBottomX + x > cutBoxStyle.value.width || clipKey.value.leftTopX + x < 0 || clipKey.value.leftTopY + y < 0 || clipKey.value.rightBottomY + y > cutBoxStyle.value.height) {

    } else {
      clipKey.value.leftTopX += x
      clipKey.value.rightTopX += x
      clipKey.value.leftBottomX += x
      clipKey.value.rightBottomX += x

      clipKey.value.rightTopY += y
      clipKey.value.leftTopY += y
      clipKey.value.rightBottomY += y
      clipKey.value.leftBottomY += y
    }
    mouseLeave.value.spx = e.offsetX
    mouseLeave.value.spy = e.offsetY
  }

}
const canvas = ref()
const cropSize = ref()
const mouseUp = (e) => {
  mouseLeave.value.keydown = false
  // 裁剪图片并显示到缩略图中
  drawAndCropImage()
}
const cutBoxStyle = ref({
  width: 200,
  height: 200,
})
watch(() => img, (value) => {
  let image = new Image();
  image.src = value.value
  image.onload = () => {
    cutBoxStyle.value.height = image.height
    drawAndCropImage()
  }
}, {deep: true})

const drawAndCropImage = () => {
  const cropImg = canvas.value
  const ctx = cropImg.getContext('2d')

  cropImg.width = clipKey.value.rightBottomX - clipKey.value.leftTopX
  cropImg.height = clipKey.value.rightBottomY - clipKey.value.leftTopY
  let image = new Image();
  image.src = img.value
  let rateY = cropSize.value.clientHeight / image.height
  let rateX = cropSize.value.clientWidth / image.width
  image.onload = () => {
    ctx.drawImage(image, clipKey.value.leftTopX, clipKey.value.leftTopY, cropImg.width / rateX, cropImg.height / rateY, 0, 0, cropImg.width, cropImg.height)
    crop.value = cropImg.toDataURL('image/png')
  }
}

let emits = defineEmits(['emitAvatar'])
let props = defineProps({
  ping: String
})
watch(() => props.ping, (value) => {
  emits('emitAvatar', crop)
}, {deep: true})
</script>

<template>
  <!-- 上传头像组件 -->
  <div class="container">
    <a-row :gutter="[16,16]">
      <a-col :span="12">
        <!-- 原始图片展示区域 -->
        <div class="source-img-wrapper">
          <img :src="img===''?defaultImg:img"
               :style="{
              opacity: '0.5',
              height: 'auto',
              width: '200px',
               }"
          />
        </div>
        <div v-if="img !== ''" class="img-crop-wrapper">
          <img ref="cropSize" :src="img"
               :style="{
            left:'100px',
            width: '200px',
            height: 'auto',
            clipPath: `polygon(${clipKey.leftTopX}px ${clipKey.leftTopY}px, ${clipKey.rightTopX}px ${clipKey.rightTopY}px, ${clipKey.rightBottomX}px ${clipKey.rightBottomY}px, ${clipKey.leftBottomX}px ${clipKey.leftBottomY}px)`,
          }"
          />
          <!-- 裁剪盒子 -->
          <div class="cut-box" @mousedown="boxMove($event)" @mousemove="mouseMove($event)" @mouseup="mouseUp($event)"
               :style="{display: 'inline-block',position:'absolute',top: 0,left: 0
               ,height:`${cutBoxStyle.height}px`
               ,width:`${cutBoxStyle.width}px`}">
            <div class="box-corner"
                 :style="{cursor:'nw-resize',top: `calc(${clipKey.leftTopY}px - 3px)`,left: `calc(${clipKey.leftTopX}px - 3px)`}"></div>
            <div class="box-corner"
                 :style="{cursor:'ne-resize',top: `calc(${clipKey.rightTopY}px - 3px)`,left: `calc(${clipKey.rightTopX}px - 3px)`}"></div>
            <div class="box-corner"
                 :style="{cursor:'se-resize',top: `calc(${clipKey.rightBottomY}px - 3px)`,left: `calc(${clipKey.rightBottomX}px - 3px)`}"></div>
            <div class="box-corner"
                 :style="{cursor:'sw-resize',top: `calc(${clipKey.leftBottomY}px - 3px)`,left: `calc(${clipKey.leftBottomX}px - 3px)`}"></div>
            <!--            <div class="box-middle"-->
            <!--                 :style="{cursor:'n-resize',top: `calc(${clipKey.leftTopY}px - 3px)`,left: `calc(${clipKey.rightTopX}px/2 - ${clipKey.leftTopX}px/2 - 20px)`}"></div>-->
            <!--            <div class="box-middle"-->
            <!--                 :style="{cursor:'s-resize',top: `calc(${clipKey.leftBottomY}px - 3px)`,left: `calc(${clipKey.rightTopX}px/2 - ${clipKey.leftTopX}px/2 - 20px)`}"></div>-->
            <!--            <div class="box-middle"-->
            <!--                 :style="{cursor:'w-resize',transform: 'rotate(90deg)',transformOrigin: 'left',top: `calc(${clipKey.leftBottomY}px/2 - ${clipKey.leftTopY}px/2 - 20px)`,left: `calc(${clipKey.leftTopX}px)`}"></div>-->
            <!--            <div class="box-middle"-->
            <!--                 :style="{cursor:'e-resize',transform: 'rotate(90deg)',transformOrigin: 'left',top: `calc(${clipKey.rightBottomY}px/2 - ${clipKey.rightTopY}px/2 - 20px)`,left: `calc(${clipKey.rightTopX}px)`}"></div>-->
          </div>
        </div>
      </a-col>
      <a-col :span="12">
        <!-- 图片裁剪结果展示区域，以及操作区域 -->
        <div class="avatar-wrapper">
          <a-image
              :width="96"
              :height="96"
              fallback="data:image/png;base64,"
              :src="crop"
              class="avatar-rectangle"
          />
          <a-image
              :width="96"
              :height="96"
              fallback="data:image/png;base64,"
              :src="crop"
              style="border-radius: 50%"
              class="avatar-circle"
          />
        </div>
        <a-input type="file" @change="uploadFile"></a-input>

        <div style="display: none;">
          <canvas ref="canvas"></canvas>
        </div>
      </a-col>
    </a-row>
  </div>
</template>

<style scoped>
.avatar-wrapper {
  display: inline-block;
  position: relative;
  width: 15em;
  height: 7em;
}

.source-img-wrapper {
  background-color: #2f2f2f;
  width: 200px;
}

.img-crop-wrapper {
  position: absolute;
  top: 0;
  width: 200px;

}

/* 使用样式穿透改变vue组件的样式 */
.avatar-wrapper :deep(.ant-image):first-child {
  display: inline-block;
  position: absolute;
  left: 0;
}

.avatar-wrapper :deep(.ant-image):last-child {
  display: inline-block;
  position: absolute;
  right: 0;
}

.box-corner {
  display: inline-block;
  position: absolute;
  border-radius: 50%;
  background-color: aqua;
  width: 6px;
  height: 6px;
}

.box-middle {
  display: inline-block;
  position: absolute;
  border-radius: 2px;
  background-color: aqua;
  width: 40px;
  height: 6px;
}
</style>